var labelArray = ["", ];
var relatedPostConfig = {
    homePage: "",
    widgetTitle: "<div class='judul-produk-lainnya'><h3></h3></div>",
    numPosts: 4,
    summaryLength: 0,
    titleLength: "auto",
    thumbnailSize: 250,
    noImage: "//3.bp.blogspot.com/-ltyYh4ysBHI/U04MKlHc6pI/AAAAAAAADQo/PFxXaGZu9PQ/w300-h375-c/no-image.png",
    containerId: "related-post-",
    newTabLink: false,
    moreText: "Read More",
    widgetStyle: 3,
